import React, {useState} from "react";
import { useNavigate, useParams } from "react-router-dom";
import styled from "styled-components";
import CommentList from "../List/CommentList";
import TextInput from "../UI/TextInput";
import Button from "../UI/Button";
import { data } from "react-router-dom";

const Wrapper = styled.div`
  padding: 16px;
  width: calc(100% - 32px);
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  justify-content: center;
`;

const Container = styled.div`
    width: 100%;
    max-width: 720px;
    & > * {
        :not(:last-child) {
            margin-bottom: 16px;
        }
    }
`;

const PostContainer = styled.div`
    padding: 8px 16px;
    border: 1px solid grey;
    border-radius: 8px;
`;

const TitleText = styled.h2`
  font-size: 28px;
  font-weight: 500;
`;

const ContentText = styled.p`
    font-size: 20px;
    line-height: 32px;
    white-space: pre-wrap;
`;

const CommentLabel = styled.p`
    font-size: 16px;
    font-weight: 500;
`;

// 글 상세 보기, 댓글 작성/삭제, 글 삭제 및 수정 버튼
function PostViewPage({ post, onDeletePost, onUpdatePost, onAddComment, onDeleteComment }) {
    const [comment, setComment] = useState("");
    const navigate = useNavigate();

    if (!post) return <div>글을 찾을 수 없습니다.</div>;

    return (
      <Wrapper>
        <Container>
          <TitleText>{post.title}</TitleText>
          <ContentText>{post.content}</ContentText>

          <Button title="수정" onClick={() => onUpdatePost(post)} />
          <Button title="삭제" onClick={() => { onDeletePost(post.id); navigate("/"); }} />

          <CommentLabel>댓글</CommentLabel>
          <CommentList
            comments={post.comments}
            onDeleteComment={(commentId) => onDeleteComment(post.id, commentId)}
          />

          <TextInput value={comment} onChange={(e) => setComment(e.target.value)} height={40} />
          <Button
            title="댓글 작성"
            onClick={() => {
              onAddComment(post.id, comment);
              setComment("");
            }}
          />
        </Container>
      </Wrapper>
    );
  }

